import React, { useState, useEffect } from 'react';
import { skillsData } from '../data/portfolioData';

const Skills = () => {
  const [animatedSkills, setAnimatedSkills] = useState([]);

  useEffect(() => {
    const timer = setTimeout(() => {
      setAnimatedSkills(skillsData);
    }, 500);
    return () => clearTimeout(timer);
  }, []);

  return (
    <section id="skills" className="section-padding">
      <div className="container">
        <h2 className="section-title text-center mb-5">مهاراتي</h2>
        <div className="row g-4">
          {skillsData.map((skill, index) => (
            <div className="col-lg-4 col-md-6" key={index} data-aos="zoom-in" data-aos-delay={index * 100}>
              <div className="skill-card">
                <div className="skill-icon">
                  <i className={skill.icon}></i>
                </div>
                <h3 className="skill-title">{skill.name}</h3>
                <div className="progress-container">
                  <div 
                    className="progress-bar"
                    style={{ 
                      width: animatedSkills.length > 0 ? `${skill.level}%` : '0%',
                      transition: 'width 1.5s ease-in-out'
                    }}
                  ></div>
                </div>
                <span className="skill-percentage">{skill.level}%</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
